// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [urheilijat, setUrheilijat] = useState([]);
  const [uusiUrheilija, setUusiUrheilija] = useState({
    etunimi: '',
    sukunimi: '',
    syntymavuosi: '',
    paino: '',
    kuvalinkki: '',
    laji: '',
    saavutukset: '',
  });

  useEffect(() => {
    axios.get('http://localhost:3006/urheilijat')
      .then(response => setUrheilijat(response.data))
      .catch(error => console.error('Virhe urheilijoiden hakemisessa:', error));
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setUusiUrheilija((prevUrheilija) => ({
      ...prevUrheilija,
      [name]: value,
    }));

    // Lisää validaatio syntymävuodelle
    if (name === 'syntymavuosi') {
      validateSyntymavuosi(value);
    }
  };

  const validateSyntymavuosi = (value) => {
    const regex = /^\d{4}-\d{2}-\d{2}$/;
    if (!regex.test(value)) {
      console.error('Virheellinen syntymävuoden muoto. Oikea muoto on YYYY-MM-DD.');
    }
  };

  const lisaaUrheilija = async () => {
    try {
      // Lisää tarkistuksia syötteiden validoinnille, esim. etunimi ja sukunimi ovat pakollisia
      if (uusiUrheilija.etunimi && uusiUrheilija.sukunimi) {
        const response = await axios.post('http://localhost:3006/urheilijat', uusiUrheilija);
        setUrheilijat([...urheilijat, response.data]);
        // Nollaa lomake
        setUusiUrheilija({
          etunimi: '',
          sukunimi: '',
          syntymavuosi: '',
          paino: '',
          kuvalinkki: '',
          laji: '',
          saavutukset: '',
        });
      } else {
        console.error('Etunimi ja sukunimi ovat pakollisia.');
      }
    } catch (error) {
      console.error('Virhe urheilijan lisäämisessä:', error);
    }
  };

  return (
    <div>
      <h2>Urheilijat</h2>
      <ul>
        {urheilijat.map((urheilija) => (
          <li key={urheilija.id}>
            {urheilija.etunimi} {urheilija.sukunimi}
          </li>
        ))}
      </ul>
      <h3>Lisää uusi urheilija:</h3>
      <form>
        <label>
          Etunimi:
          <input type="text" name="etunimi" value={uusiUrheilija.etunimi} onChange={handleInputChange} />
        </label>
        <label>
          Sukunimi:
          <input type="text" name="sukunimi" value={uusiUrheilija.sukunimi} onChange={handleInputChange} />
        </label>
        <label>
        syntymavuosi:
          <input type="text" name="syntymavuosi" value={uusiUrheilija.syntymavuosi} onChange={handleInputChange} />
        </label>
        <label>
        paino:
          <input type="text" name="paino" value={uusiUrheilija.paino} onChange={handleInputChange} />
          <label>
          kuvalinkki:
          <input type="text" name="kuvalinkki" value={uusiUrheilija.kuvalinkki} onChange={handleInputChange} />
        </label>
        <label>
        laji:
          <input type="text" name="laji" value={uusiUrheilija.laji} onChange={handleInputChange} />
        </label>
        <label>
        saavutukset:
          <input type="text" name="saavutukset" value={uusiUrheilija.saavutukset} onChange={handleInputChange} />
        </label>
        </label>
        {/* Lisää muut kentät vastaavasti */}
        <button type="button" onClick={lisaaUrheilija}>
          Lisää urheilija
        </button>
      </form>
    </div>
  );
};

export default App;
