import React, { useState, useEffect, createContext, useContext } from 'react';
import axios from 'axios';

const UrheilijatContext = createContext();

const UrheilijatProvider = ({ children }) => {
  const [urheilijat, setUrheilijat] = useState([]);

  useEffect(() => {
    // Hae urheilijat tietokannasta
    axios.get('http://localhost:3006/urheilijat').then((response) => {
      setUrheilijat(response.data);
    });
  }, []);

  const lisaaUrheilija = (uusiUrheilija) => {
    // Lisää uusi urheilija tietokantaan
    axios.post('http://localhost:3006/urheilijat', uusiUrheilija).then(() => {
      setUrheilijat([...urheilijat, uusiUrheilija]);
    });
  };

  // Muut operaatiot (päivitys, poisto) toteutetaan samalla tavalla.

  return (
    <UrheilijatContext.Provider value={{ urheilijat, lisaaUrheilija }}>
      {children}
    </UrheilijatContext.Provider>
  );
};

const UrheilijaList = () => {
  const { urheilijat, lisaaUrheilija } = useContext(UrheilijatContext);

  return (
    <div>
      <ul>
        {urheilijat.map((urheilija) => (
          <li key={urheilija.id}>{urheilija.etunimi} {urheilija.sukunimi}</li>
        ))}
      </ul>
      <button onClick={() => lisaaUrheilija({ etunimi: 'Uusi', sukunimi: 'Urheilija' })}>
        Lisää uusi urheilija
      </button>
    </div>
  );
};

const App = () => {
  return (
    <UrheilijatProvider>
      <UrheilijaList />
    </UrheilijatProvider>
  );
};

export default App;
