// App.js
import React, { useState, useEffect, createContext, useContext } from 'react';
import axios from 'axios';

const UrheilijatContext = createContext();

const UrheilijatProvider = ({ children }) => {
  const [urheilijat, setUrheilijat] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3006/urheilijat')
      .then(response => setUrheilijat(response.data))
      .catch(error => console.error('Virhe urheilijoiden hakemisessa:', error));
  }, []);

  const lisaaUrheilija = async (uusiUrheilija) => {
    try {
      const response = await axios.post('http://localhost:3006/urheilijat', uusiUrheilija);
      setUrheilijat([...urheilijat, response.data]);
    } catch (error) {
      console.error('Virhe urheilijan lisäämisessä:', error);
    }
  };

  // Lisää muut operaatiot (päivitys, poisto) samalla tavalla.

  return (
    <UrheilijatContext.Provider value={{ urheilijat, lisaaUrheilija }}>
      {children}
    </UrheilijatContext.Provider>
  );
};

const UrheilijaList = () => {
  const { urheilijat, lisaaUrheilija } = useContext(UrheilijatContext);

  return (
    <div>
      <h2>Urheilijat</h2>
      <ul>
        {urheilijat.map(urheilija => (
          <li key={urheilija.id}>
            {urheilija.etunimi} {urheilija.sukunimi}
          </li>
        ))}
      </ul>
      <button onClick={() => lisaaUrheilija({ etunimi: 'Uusi', sukunimi: 'Urheilija' })}>
        Lisää uusi urheilija
      </button>
    </div>
  );
};

const App = () => {
  return (
    <UrheilijatProvider>
      <UrheilijaList />
    </UrheilijatProvider>
  );
};

export default App;
