import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [urheilijat, setUrheilijat] = useState([]);
  const [uusiUrheilija, setUusiUrheilija] = useState({
    etunimi: '',
    sukunimi: '',
    syntymavuosi: '',
    paino: '',
    kuvalinkki: '',
    laji: '',
    saavutukset: '',
  });
  const [muokattavaUrheilija, setMuokattavaUrheilija] = useState(null);

  useEffect(() => {
    haeUrheilijat();
  }, []);

  const haeUrheilijat = async () => {
    try {
      const response = await axios.get('http://localhost:3006/urheilijat');
      setUrheilijat(response.data);
    } catch (error) {
      console.error('Virhe urheilijoiden hakemisessa:', error);
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    if (name === 'syntymavuosi' && !isValidDate(value)) {
      console.error('Syntymävuosi on virheellisessä muodossa.');
      return;
    }
    setUusiUrheilija((prevUrheilija) => ({
      ...prevUrheilija,
      [name]: value,
    }));
  };

  const isValidDate = (dateString) => {
    const regex = /^\d{4}-\d{2}-\d{2}$/;
    return regex.test(dateString);
  };

// ...

const lisaaUrheilija = async () => {
  try {
    if (uusiUrheilija.etunimi && uusiUrheilija.sukunimi) {
      // Muuta syntymävuosi oikeaan muotoon
      const syntymavuosiDate = new Date(uusiUrheilija.syntymavuosi);
      uusiUrheilija.syntymavuosi = syntymavuosiDate.toISOString().split('T')[0];

      const response = await axios.post('http://localhost:3006/urheilijat', uusiUrheilija);
      setUrheilijat([...urheilijat, response.data]);
      setUusiUrheilija({
        etunimi: '',
        sukunimi: '',
        syntymavuosi: '',
        paino: '',
        kuvalinkki: '',
        laji: '',
        saavutukset: '',
      });
    } else {
      console.error('Etunimi ja sukunimi ovat pakollisia.');
    }
  } catch (error) {
    console.error('Virhe urheilijan lisäämisessä:', error);
  }
};




const paivitaUrheilija = async () => {
  try {
    if (muokattavaUrheilija && muokattavaUrheilija.id) {
      // Muuta syntymävuosi oikeaan muotoon vain päivityksen yhteydessä
      const syntymavuosiDate = new Date(muokattavaUrheilija.syntymavuosi);
      muokattavaUrheilija.syntymavuosi = syntymavuosiDate.toISOString().split('T')[0];

      await axios.put(`http://localhost:3006/urheilijat/${muokattavaUrheilija.id}`, muokattavaUrheilija);
      haeUrheilijat();
      setMuokattavaUrheilija(null);
    } else {
      console.error('Virhe päivittäessä urheilijaa. Tarkista, että muokattava urheilija on asetettu.');
    }
  } catch (error) {
    console.error('Virhe urheilijan päivittämisessä:', error);
  }
};

  const poistaUrheilija = async (urheilijaId) => {
    try {
      await axios.delete(`http://localhost:3006/urheilijat/${urheilijaId}`);
      haeUrheilijat();
    } catch (error) {
      console.error('Virhe urheilijan poistamisessa:', error);
    }
  };

  const aloitaMuokkaus = (urheilija) => {
    setMuokattavaUrheilija(urheilija);
    setUusiUrheilija(urheilija); // Tässä voit tarvittaessa asettaa vain halutut kentät uuden urheilijan lomakkeeseen
  };

  const peruutaMuokkaus = () => {
    setMuokattavaUrheilija(null);
    setUusiUrheilija({
      etunimi: '',
      sukunimi: '',
      syntymavuosi: '',
      paino: '',
      kuvalinkki: '',
      laji: '',
      saavutukset: '',
    });
  };

  return (
    <div>
      <h2>Urheilijat</h2>
      <ul>
        {urheilijat.map((urheilija) => (
          <li key={urheilija.id}>
            {urheilija.etunimi} {urheilija.sukunimi}
            <button onClick={() => poistaUrheilija(urheilija.id)}>Poista</button>
            <button onClick={() => aloitaMuokkaus(urheilija)}>Muokkaa</button>
          </li>
        ))}
      </ul>
      <h3>Lisää uusi urheilija:</h3>
      <form>
        <label>
          Etunimi:
          <input type="text" name="etunimi" value={uusiUrheilija.etunimi} onChange={handleInputChange} />
        </label>
        <label>
          Sukunimi:
          <input type="text" name="sukunimi" value={uusiUrheilija.sukunimi} onChange={handleInputChange} />
        </label>
        <label>
          Syntymävuosi:
          <input type="text" name="syntymavuosi" placeholder="YYYY-MM-DD" value={uusiUrheilija.syntymavuosi} onChange={handleInputChange} />
        </label>
        <label>
          Paino:
          <input type="text" name="paino" value={uusiUrheilija.paino} onChange={handleInputChange} />
        </label>
        <label>
          Kuvalinkki:
          <input type="text" name="kuvalinkki" value={uusiUrheilija.kuvalinkki} onChange={handleInputChange} />
        </label>
        <label>
          Laji:
          <input type="text" name="laji" value={uusiUrheilija.laji} onChange={handleInputChange} />
        </label>
        <label>
          Saavutukset:
          <input type="text" name="saavutukset" value={uusiUrheilija.saavutukset} onChange={handleInputChange} />
        </label>
        {muokattavaUrheilija ? (
          <>
            <button type="button" onClick={paivitaUrheilija}>
              Päivitä urheilija
            </button>
            <button type="button" onClick={peruutaMuokkaus}>
              Peruuta muokkaus
            </button>
          </>
        ) : (
          <button type="button" onClick={lisaaUrheilija}>
            Lisää urheilija
          </button>
        )}
      </form>
    </div>
  );
};

export default App;
